function test(event){
  if(event.code === 'Enter'){
    alert(
      'Value: ' + this.value + '\n' + 
      'Checked: ' + this.checked
    )
  }
}
let inputs = document.querySelectorAll("input")
for(let i in inputs){
  inputs[i].addEventListener('keypress', test)
}

