css:
p{
  font-size:1.5em;
  padding: 10px;
}

div{ font-weight: bold; }
.class_name { color: green;}
#id_name { 
  color: red; 
}

.view input{ font-size: 1em; border-width: 15px; width: 100%;}
[value]{ border-color: red;} 
[type=email]{border-color: green;}
p[type]{ border: 1px solid;}

input{ font-size: 1em; }
input[value*=один]{ color: red;}
input[value~=два]{ text-decoration: underline;}

input{ font-size: 1em; }
input[value^=число]{ color: red;}
input[value|=число]{ color: green;}
input[value$=три]{ text-decoration: underline;}

.special span{ color: blue;}
span{ color: red;}
span{ color: green;}

.special span{ color: blue;}
span{ color: red!important;}
span{ color: green;}

a{color: green;}
.view .yell{color: yellow;}
.red{color: red!important;}

span:hover{  color: red; }
.v1 span:hover{ color:red;}
.v1:hover span {font-size: 120%;}

.autohide{background: #cccccc; padding: 1em}
.hidden{ dispay: none;}
.autohide:hover .hidden{ display: block;}

nav{
  width: 400px;
  position: sticky;
  top: 0px;
}
nav ul{
  padding: 0; 
  background: #cccccc; 
  border: 1px solid black;
  display: flex;
  justify-content: space-around;
}
nav li{list-style: none; position: relative;}
nav li > ul {
  display: none; 
  position: absolute;
}
nav li:hover > ul{display: block;}
/*nav li > ul ul{
  left: 100%;
  top: 0;
}*/

input{ font-size: 1em;}
input:focus{ border-left: 1em solid red;}
input:required{ border-color: red;}
input:valid{color: green; }

input[type=checkbox]{transform: scale(2); margin: 20px;}
input:indeterminate + span{ color: green;}
input:checked + span{ color: red;}

span{ font-weight: bold;}
span:nth-child(2){ color:red;}
span:nth-of-type(2){ color: green;}

q:lang(en) {
  quotes: "\201C" "\201D"; }
p:lang(fr), q:lang(ru) { quotes: "\00AB" "\00BB"; }

p:first-letter{font-family:fantasy;font-size: 2em;}
p:first-line{color: green;}
p::after{ 
content: ', которого нет в исходном коде текста.'
}/*аналог - before, но в начале*/

li{list-style: none;}
li::before{ content: ""; display: inline-block;
width: 20px; height: 15px; 
background: url('logo.png') no-repeat left center;
background-size: 15px auto;
}

ul{background: #cccccc;}
li{list-style: none; float: left; margin-right: 10px;}
ul:after{content: ""; display: block; clear:both;}
